import React, { useContext } from "react";
import "./CartItems.css";
import { ShopContext } from "../../context/ShopContext";
import remove_item from "../../assets/cart_cross_icon.png";
const CartItems = () => {
  const { all_product, cartItems, addToCart, removeFromCart } =
    useContext(ShopContext);
  return (
    <div className="cart-items">
      <div className="cart-items-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {all_product.map((product, index) => {
        if (cartItems[product.id] > 0) {
          return (
            <div>
              <div className="cart-items-format">
                <img src={product.image} alt="" className="cart-product-icon" />
                <p>{product.name}</p>
                <p>${product.new_price}</p>
                <button className="cart-items-quantity">
                  {cartItems[product.id]}
                </button>
                <p>{cartItems[product.id] * product.new_price}</p>
                <img
                  src={remove_item}
                  alt=""
                  onClick={() => {
                    removeFromCart();
                  }}
                />
              </div>
            </div>
          );
        }
      })}
    </div>
  );
};

export default CartItems;
